import { useEffect, useState } from "react";
import SearchBar from "../componets/Main/SearchCountry";
import RegionFilter from "../components/Main/RegionFilter";
import { Link } from "react-router-dom";
import "../styles/Main.css";

function Main() {
  const [countryList, setCountryList] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [regions, setRegions] = useState([]);
  const [selectedRegion, setSelectedRegion] = useState("All");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://localhost:4040/api/countries");
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const countries = await response.json();
        setCountryList(countries);

        const uniqueRegions = Array.from(
          new Set(countries.map((country) => country.region))
        );
        setRegions(uniqueRegions);
      } catch (error) {
        console.error("Error:", error);
      }
    };

    fetchData();
  }, []);

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSelectRegion = (region) => {
    setSelectedRegion(region);
  };

  return (
    <div className="main-container">
      <div className="filters-conteiner">
        <SearchBar searchTerm={searchTerm} onSearchChange={handleSearch} />
        <RegionFilter
          regions={regions}
          selectedRegion={selectedRegion}
          onSelectRegion={handleSelectRegion}
        />
      </div>
      <div className="country-grid">
        {countryList.map((country) => (
          <Link
            key={country.name}
            to={`/details/${encodeURIComponent(country.name)}`}
            className="country-link"
          >
            <div className="card">
              <img src={country.flags.svg} alt={`Flag of ${country.name}`} />
              <h3>{country.name}</h3>
              <p>
                <strong>Population:</strong> {country.population}
              </p>
              <p>
                <strong>Region:</strong> {country.region}
              </p>
              <p>
                <strong>Capital:</strong> {country.capital}
              </p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}

export default Main;
